mapper.insert:

            SELECT 'FL-' || TO_CHAR(SYSDATE, 'YYYYMMDD-') || LPAD(SEQ_FILE_PK.NEXTVAL, 6, '0')
              FROM DUAL

        INSERT INTO "FILE" 
		 (FILE_ID
		, FILE_GROUP_ID
		, FILE_SIZE
		, FILE_DISPLAY_NAME
		, FILE_NAME
		, FILE_PATH
		, FILE_DOWNLOAD_COUNT
		, FILE_TYPE) 
		VALUES
		 (#{fileId}
		, #{fileGroupId}
		, #{fileSize}
		, #{fileDisplayName}
		, #{fileName}
		, #{filePath}
		, 0 
		, #{fileType})
